var http = require("http");
var domain = require("domain");

http.createServer(function(req, res){
    var d = domain.create();
    d.on("error", function(err){
        console.log(err);
        d.dispose();
    });
    d.add(req);
    d.add(res);
//    d.run(function(){throw "EEE"});
    d.run(function(){
        res.redirect("http://www.baidu.com");
    })
    console.log("SDFSDF");
}).listen(3000, function(err){
        err ? console.error(err) : console.log("port : 3000")
    });
