var INFO = "info";
var WARN = "warning";
var ERR = "err";
var QUEST = "quest";

function ResultVo(type, msg, value){
    this.msgType = type;
    this.msg = msg;
    this.value = value;
}

function doInfo(res, msg, value){
    res.json(new ResultVo(INFO, msg || "Success!", value));
};

function doWarn(res, msg, value){
    res.json(new ResultVo(WARN, msg || "Warning!", value));
};

function doErr(res, msg, value){
    res.json(new ResultVo(ERR, msg, value));
};

function doQuest(res, msg, value){
    res.json(new ResultVo(QUEST, msg, value));
};

module.exports = {
    doInfo : doInfo,
    doWarn : doWarn,
    doErr : doErr,
    doQuest : doQuest
};